import React from 'react';
import serialize from 'form-serialize';

class AddUser extends React.Component {

    handleFormSubmit = (e) => {
        e.preventDefault();
        const newUser = serialize(e.target, { hash: true });
        //console.log(newUser);
        this.props.onAddUser(newUser);
    }

    //input alanları boş olmasın diye inputlar boş olduğunda kaydet butonu pasif oluyor
    //bunun için state ve setState tanımladım.
    state ={
        name:"",
        surname:"",
        tel:""
    }

    handleChangeName = (e)=>{
        this.setState({
          name:e.target.value
        })
    }

    handleChangeSurName = (e)=>{
        this.setState({
          surname:e.target.value
        })
    }

    handleChangeTel = (e)=>{
        this.setState({
          tel:e.target.value
        })
    }

    constructor(props){
        super(props);

        this.state = {
            fields: {},
            errors: {}
        }
     }

     handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Name
        if(!fields["name"]){
           formIsValid = false;
           errors["name"] = "Cannot be empty";
        }

        if(typeof fields["name"] !== "undefined"){
           if(!fields["name"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["name"] = "Only letters";
           }        
        }

        //Email
        if(!fields["surname"]){
           formIsValid = false;
           errors["surname"] = "Cannot be empty";
        }

        if(typeof fields["surname"] !== "undefined"){
            if(!fields["surname"].match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["surname"] = "Only letters";
            }        
         }
         this.setState({errors: errors});
           return formIsValid;

    }

    contactSubmit(e){
        e.preventDefault();

        if(this.handleValidation()){
           alert("Form submitted");
        }else{
           alert("Form has errors.")
        }

    }

    handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
    }





    render() {

        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>

                <input className="form-control" id="disabledInput" type="text" placeholder="Kişi kaydetmek için alanları doldurun" disabled/>
                    <div className="form-row">
                        <div className="form-group col-md-12 mt-4">
                            <label htmlFor="inputName">İsim</label>
                            <input id="deneme" type="text" 
                                    className="form-control" 
                                    name="name" 
                                    // value={this.state.name} onChange={this.handleChangeName}

                                    onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]}

                                />

                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="inputSurName">Soyisim</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="surname"
                                    value={this.state.surname} onChange={this.handleChangeSurName}
                                    />
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="telTextarea">Telefon Numarası</label>
                            <input 
                                type="number"
                                className="form-control" 
                                name="tel"
                                value={this.state.tel} onChange={this.handleChangeTel}/>
                        </div>

                    </div>
                <input type="submit" className="btn btn-success btn-block" value="Kişiyi kaydet"
                // disabled={(this.state.name.length<1), (this.state.surname.length<1), (this.state.tel.length<11) }
                 />

            </form>
        </div>
        )

    }
}


export default AddUser;
