import React from 'react';
import axios from 'axios';

class EditMovie extends React.Component {

    state = {
        name: "",
        surname: "",
        tel: "",
    }

    async componentDidMount() {

        const id = this.props.match.params.id;
        //console.log(id)

        const response = await axios.get(`http://localhost:3002/users/${id}`);
        //console.log(response.data);

        const movie = response.data;

        this.setState({
            name: movie.name,
            surname: movie.surname,
            tel: movie.tel,
        })

    }

    onInputChange = (e) => {
    //    console.log(e.target.name);
     //  console.log(e.target.value);

        this.setState({
            [e.target.name]: e.target.value,
            //[e.target.surname]: e.target.value,
            //[e.target.tel]: e.target.value
        })
    }

    handleFormSubmit = (e) => {
        e.preventDefault();
/* 
        const name = this.state.name;
        const tel = this.state.tel; */

        const { name,surname, tel } = this.state;

        const id = this.props.match.params.id;

        const updatedMovie = {
            name,
            surname,
            tel,
        }

        this.props.onEditMovie(id, updatedMovie);
        this.props.history.push('/');
    }

    render() {

        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>
            <input className="form-control" id="disabledInput" type="text" placeholder="Kişi bilgilerini güncelleyin" disabled/>
                <div className="form-row">
                    <div className="form-group col-md-12 mt-4">
                        <label htmlFor="inputName">İsim:</label>
                        <input  type="text" 
                                className="form-control " 
                                name="name"
                                value={this.state.name}
                                onChange={this.onInputChange}/>
                    </div>
                </div>

                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label htmlFor="inputSurName">Soyisim:</label>
                        <input  type="text" 
                                className="form-control " 
                                name="surname"
                                value={this.state.surname}
                                onChange={this.onInputChange}/>
                    </div>
                </div>

                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label htmlFor="telTextarea">Telefon Numarası:</label>
                        <input 
                                className="form-control" 
                                name="tel" 
                                value={this.state.tel}
                                onChange={this.onInputChange}/>
                    </div>
                </div>
                <input type="submit" className="btn btn-info btn-block" value="Kişiyi Bilgilerini Güncelle" />
            </form>
        </div>
        )

    }
}


export default EditMovie;
