import React from 'react';
import serialize from 'form-serialize';
import { Textbox } from 'react-inputs-validation';

class AddUser extends React.Component {

    handleFormSubmit = (e) => {
        e.preventDefault();
        const newUser = serialize(e.target, { hash: true });
        //console.log(newUser);
        this.props.onAddUser(newUser);
    }

    render() {

        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>
                <input className="form-control" id="disabledInput" type="text" placeholder="Kişi kaydetmek için alanları doldurun" disabled/>
                    <div className="form-row">
                        <div className="form-group col-md-12 mt-4">
                            <label htmlFor="inputName">İsim:</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="name" 

                                    onChange={(name, e) => {
                                        this.setState({ name });
                                        console.log(e);
                                      }} 

                                      onBlur={(e) => {console.log(e)}} 

                                    validationOption={{
                                        name: 'Name', 
                                        check: true, 
                                        required: true 
                                    }}

                            />
                        </div>

                        <Textbox
                            attributesInput={{ // Optional.
                                id: 'Number',
                                name: 'Number',
                                type: 'text', 
                                placeholder: 'Place your number here ^-^',
                            }}
                            value={number} // Optional.[String].Default: "".
                            onChange={(number, e) => {
                                this.setState({ number });
                                console.log(e);
                            }} 
                            onBlur={() => {}} 
                            validationOption={{
                                type: 'number', 
                                min: 10, 
                                max: 100 
                            }}
                            />
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="inputSurName">Soyisim:</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="surname"/>
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="telTextarea">Telefon Numarası:</label>
                            <input 
                                type="number" maxLength="11"
                                className="form-control" 
                                name="tel"/>
                        </div>

                    </div>
                <input type="submit" className="btn btn-success btn-block" value="Kişiyi kaydet" />
            </form>
        </div>
        )

    }
}


export default AddUser;
