import React from 'react';
import { Link } from 'react-router-dom';

const MovieList = (props) => {



    const truncateOverview = (string, maxLength) => {
        if (!string) return null;
        if (string.length <= maxLength ) return string;
        return `${string.substring(0, maxLength)} ...`;
    }

        return (

            <div className="row">

                {props.movies.map((movie, i) => (



                    <div className="col-md-8 mb-1" key={i}>

                        <div className="card mb-4 shadow-sm">


                            <div className="card-body">
                                <h5 className="card-title">İsim: {movie.name}</h5>
                                <p className="card-text">Telefon Numarası: {truncateOverview(movie.overview, 100)}</p>
                                <div className="d-flex justify-content-end align-items-center">

                                    <button type="button" onClick={(event) => props.deleteMovieProp(movie)} className="btn btn-md btn-outline-danger mr-2">Sil</button>

                                    <Link type="button"
                                    className="btn btn-md btn-outline-primary"
                                    to={`edit/${movie.id}`}
                                    >Düzenle </Link>
                                </div>
                            </div>
                        </div>
                    </div>

                ))}

            </div>
        )
}

export default MovieList;
