import React from 'react';
import UserList from './UserList';
import SearchBar from './SearchBar';
import AddUser from './AddUser';
import EditUser from './EditUser';
import axios from 'axios';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

class App extends React.Component {

    state = {
        users: [],
        searchQuery: ""
    }

    componentDidMount() {
        this.getUsers();
    }

    async getUsers() {
        const response = await axios.get("http://localhost:3002/users");
        this.setState({ users: response.data })
    }

    // DELETE MOVIE
    deleteMovie = async (movie) => {

        axios.delete(`http://localhost:3002/users/${movie.id}`)
        const newUserList = this.state.users.filter(
            m => m.id !== movie.id
        );
        this.setState(state => ({
            users: newUserList
        }))
    }


    // SEARCH MOVIE
    searchMovie = (event) => {
        this.setState({ searchQuery: event.target.value })
    }


    // ADD MOVIE
    addMovie = async (movie) => {
        await axios.post(`http://localhost:3002/users/`, movie)
        this.setState(state => ({
            users: state.users.concat([movie])
        }))

        this.getUsers();
    }

        // EDIT MOVIE
        editMovie = async (id, updatedMovie) => {
            await axios.put(`http://localhost:3002/users/${id}`, updatedMovie)
            this.getUsers();
        }

    render() {

        let filteredMovies = this.state.users.filter(
            (movie) => {
                return movie.name.toLowerCase().indexOf(this.state.searchQuery.toLowerCase()) !== -1
            }
        ).sort((a, b) => {
            return a.id < b.id ? 1 : a.id > b.id ? -1 : 0;
        });

        return (
            <Router>

                <div className="container">

                    <Switch>
                        <Route path="/" exact render={() => (
                            <React.Fragment>
                                <div className="row">
                                    <div className="col-lg-12">
                                        <SearchBar searchMovieProp={this.searchMovie} />
                                    </div>
                                </div>

                                <UserList
                                    users={filteredMovies}
                                    deleteMovieProp={this.deleteMovie}

                                />
                            </React.Fragment>
                        )}>

                        </Route>

                        <Route path="/add" render={({ history }) => (
                            <AddUser
                                onAddUser={(movie) => {
                                    this.addMovie(movie)
                                    history.push("/")
                                }
                                }
                            />
                        )}>

                        </Route>

                        <Route path="/edit/:id" render={(props) => (
                            <EditUser
                                {...props}
                                onEditUser={(id, movie) => {
                                    this.editMovie(id, movie)
                                }
                                }
                            />
                        )}>
                        </Route>
                    </Switch>
                </div>
            </Router>
        )
    }
}

export default App;
