import React from 'react';
import UserList from './UserList';
import SearchBar from './SearchBar';
import AddUser from './AddUser';
import EditUser from './EditUser';
import axios from 'axios';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Popup from './Popup';

class App extends React.Component {

    state = {
        users: [],
        searchQuery: "",
    }

    componentDidMount() {
        this.getUsers();
    }

    async getUsers() {
        const response = await axios.get("http://localhost:3002/users");
        this.setState({ users: response.data })
    }

    // DELETE USER
    deleteUser = async (user) => {

        axios.delete(`http://localhost:3002/users/${user.id}`)
        const newUserList = this.state.users.filter(
            m => m.id !== user.id
        );
        this.setState(state => ({
            users: newUserList
        }))
    }

    // SEARCH USER
    searchUser = (event) => {
        this.setState({ searchQuery: event.target.value })
    }

    // ADD USER
    addUser = async (user) => {
        await axios.post(`http://localhost:3002/users/`, user)
        this.setState(state => ({
            users: state.users.concat([user])
        }))

        this.getUsers();
    }

    // EDIT USER
    editUser = async (id, updatedUser) => {
        await axios.put(`http://localhost:3002/users/${id}`, updatedUser)
        this.getUsers();
    }

    render() {

        let filteredUsers = this.state.users.filter(
            (user) => {
                return user.name.toLowerCase().indexOf(this.state.searchQuery.toLowerCase()) !== -1
            }
        ).sort((a, b) => {
            return a.id < b.id ? 1 : a.id > b.id ? -1 : 0;
        });       

        return (
            <Router>

                <div className="container">

                    <Switch>
                        <Route path="/" exact render={() => (
                            <React.Fragment>
                                <div className="row">
                                    <div className="col-lg-12">
                                        <SearchBar searchUserProp={this.searchUser} />
                                    </div>
                                </div>

                                <UserList
                                    users= {filteredUsers}
                                    deleteUserProp={this.deleteUser}
                                /> 
                            </React.Fragment>
                        )}>

                        </Route>

                        <Route path="/add" render={({ history }) => (
                            <AddUser
                                onAddUser={(user) => {
                                    this.addUser(user)
                                    history.push("/")
                                }
                                }
                            />
                        )}>

                        </Route>

                        <Route path="/edit/:id" render={(props) => (
                            <EditUser
                                {...props}
                                onEditUser={(id, user) => {
                                    this.editUser(id, user)
                                }
                                }
                            />
                        )}>
                        </Route>
                    </Switch>
                </div>
            </Router>
        )
    }
}

export default App;
