import React from 'react';
import { Link } from 'react-router-dom';

const UserList = (props) => {



    const truncateTel = (string, maxLength) => {
        if (!string) return null;
        if (string.length <= maxLength ) return string;
        return `${string.substring(0, maxLength)} ...`;
    }

        return (
            <div className="row">
                <button className="btn btn-dark btn-block mb-2 w-75">Kayıtlı Telefonlar Listesi</button>

                {props.users.map((user, i) => (

                    <div className="col-md-8 mb-1" key={i}>  
                        <div className="card mb-4 shadow-sm">

                            <div className="card-body d-flex justify-content-between">
                                <div>
                                    <h5 className="card-title">{user.name} {user.surname}</h5>
                                    <p className="card-text"> {truncateTel(user.tel, 100)}</p>
                                </div>

                                <div className="d-flex  float-right h-50 mt-2">
                                <Link type="button"
                                    className="btn btn-md btn-outline-primary  mr-2"
                                    to={`edit/${user.id}`}
                                    ><i className="fas fa-user-edit"></i> </Link>

                                    <button type="button" onClick={(event) => props.deleteUserProp(user)} className="btn btn-md btn-outline-danger">
                                    <i className="far fa-trash-alt"></i></button>

                                </div>
                            </div>
                        </div>
                    </div>

                ))}

            </div>
        )
}

export default UserList;
