import { Link } from 'react-router-dom';

class SearchBar extends React.Component {


    handleFormSubmit = (event) => {
        event.preventDefault();
    }

    state ={
        visible:false
    }
    changeVisibility=(e)=>{
        this.setState({
            visible: !this.state.visible
        })
    }

    render() {

        return  (
            <form onSubmit={this.handleFormSubmit}>
                <div className="form-row mb-3">

                    <div className="col-8">
                        <Link
                                to="/add"
                                type="button" 
                                className="btn btn-md btn-primary mt-3 mb-1"
                                style={{float:'left'}}>Yeni Kişi Ekle <i className="fas fa-user-plus ml-1"></i>
                        </Link>
                    </div>
                </div>

                <div>
                    <button onClick={this.changeVisibility} className="btn btn-dark btn-block mb-2">{visible ? "Telefon Numarası Ekleme Formunu Gizle" : "Telefon Numarası Ekleme Formunu Göster"}</button>

                      <div className="card">
                           <div className="card-header">
                                <h4>Telefon Numarası Ekleme Formu</h4>
                            </div>
                        </div>                 
                </div>
            </form>
        )

    }
}


export default SearchBar;
