import React from 'react';
import serialize from 'form-serialize';


class AddUser extends React.Component {

    handleFormSubmit = (e) => {
        e.preventDefault();
        const newUser = serialize(e.target, { hash: true });
        //console.log(newUser);
        this.props.onAddUser(newUser);
    }

    //input alanları boş olmasın diye inputlar boş olduğunda kaydet butonu pasif oluyor
    //bunun için state ve setState tanımladım.
    state ={
        name:"",
        surname:"",
        tel:""
    }

    handleChangeName = (e)=>{
        this.setState({
          name:e.target.value
        })
    }

    handleChangeSurName = (e)=>{
        this.setState({
          surname:e.target.value
        })
    }

    handleChangeTel = (e)=>{
        this.setState({
          tel:e.target.value
        })
    }

     requiredValidator = (value) => {
        return value ? "" : "This field is required";
      }

     emailValidator = (value) => (
        new RegExp(/\S+@\S+\.\S+/).test(value) ? "" : "Please enter a valid email."
      );




       Input = (fieldProps) => {
        const { fieldType, label, onChange, value } = fieldProps;

        return (
          <div>
            <label>
              { label }
              <input
                type={fieldType}
                value={value}
                onChange={onChange} />
            </label>
          </div>
        );
      };

       Input = (fieldProps) => {
        const {
          fieldType, label, onChange, value,
          valid, visited, validationMessage
        } = fieldProps;
        const invalid = !valid && visited;

        return (
          <div>
            <label>
              { label }
              <input
                type={fieldType}
                className={invalid ? "invalid" : ""}
                value={value}
                onChange={onChange} />
            </label>
            { invalid && 
              (<div className="required">{validationMessage}</div>) }
          </div>
        );
      };


    render(renderProps) {


        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>

                <input className="form-control" id="disabledInput" type="text" placeholder="Kişi kaydetmek için alanları doldurun" disabled/>
                    <div className="form-row">
                        <div className="form-group col-md-12 mt-4">
                            <label htmlFor="inputName">İsim</label>
                            <input id="deneme" type="text" 
                                    className="form-control" 
                                    name="name" 
                                    // value={this.state.name} onChange={this.handleChangeName}
                                    component={Input}
                                validator={[requiredValidator, emailValidator]}
                                disabled={!renderProps.allowSubmit}

                                />

                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="inputSurName">Soyisim</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="surname"
                                    value={this.state.surname} onChange={this.handleChangeSurName}
                                    />
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="telTextarea">Telefon Numarası</label>
                            <input 
                                type="number"
                                className="form-control" 
                                name="tel"
                                value={this.state.tel} onChange={this.handleChangeTel}/>
                        </div>

                    </div>
                <input type="submit" className="btn btn-success btn-block" value="Kişiyi kaydet"
                disabled={(this.state.name.length<1), (this.state.surname.length<1), (this.state.tel.length<11) }
                 />

            </form>
        </div>
        )

    }
}


export default AddUser;
