import React from 'react';
import axios from 'axios';

class EditUser extends React.Component {

    state = {
        name: "",
        surname: "",
        tel: "",
    }

    async componentDidMount() {

        const id = this.props.match.params.id;
        //console.log(id)

        const response = await axios.get(`http://localhost:3002/users/${id}`);
        //console.log(response.data);

        const user = response.data;

        this.setState({
            name: user.name,
            surname: user.surname,
            tel: user.tel,
        })

    }

    onInputChange = (e) => {

        this.setState({
            [e.target.name]: e.target.value,
            //[e.target.surname]: e.target.value,
            //[e.target.tel]: e.target.value
        })
    }

    handleFormSubmit = (e) => {
        e.preventDefault();

        const { name,surname, tel } = this.state;
        const id = this.props.match.params.id;

        const updatedUser = {
            name,
            surname,
            tel,
        }

        this.props.onEditUser(id, updatedUser);
        this.props.history.push('/');
    }

    handleChangeName = (e)=>{
        this.setState({
          name:e.target.value
        })
    }

    handleChangeSurName = (e)=>{
        this.setState({
          surname:e.target.value
        })
    }

    handleChangeTel = (e)=>{
        this.setState({
          tel:e.target.value
        })
    }

    render() {

        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>
            <input className="form-control" id="disabledInput" type="text" placeholder="Kişi bilgilerini güncelleyin" disabled/>
                <div className="form-row">
                    <div className="form-group col-md-12 mt-4">
                        <label htmlFor="inputName">İsim</label>
                        <input  type="text" 
                                className="form-control " 
                                name="name"
                                value={this.state.name}
                                onChange={this.onInputChange & this.handleChangeName}
                                />
                    </div>
                </div>

                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label htmlFor="inputSurName">Soyisim</label>
                        <input  type="text" 
                                className="form-control " 
                                name="surname"
                                value={this.state.surname}
                                onChange={this.onInputChange, this.handleChangeSurName}
                                />
                    </div>
                </div>

                <div className="form-row">
                    <div className="form-group col-md-12">
                        <label htmlFor="telTextarea">Telefon Numarası</label>
                        <input 
                                className="form-control" 
                                name="tel" 
                                value={this.state.tel}
                                onChange={this.onInputChange, this.handleChangeTel}
                                />
                    </div>
                </div>
                <input type="submit" className="btn btn-info btn-block" value="Kişiyi Bilgilerini Güncelle"
                disabled={(this.state.name.length<1), (this.state.surname.length<1), (this.state.tel.length<11)} />
            </form>
        </div>
        )

    }
}


export default EditUser;
