import React from 'react';
import serialize from 'form-serialize';

class AddMovie extends React.Component {

    handleFormSubmit = (e) => {
        e.preventDefault();
        const newMovie = serialize(e.target, { hash: true });
        //console.log(newMovie);
        this.props.onAddMovie(newMovie);
    }


    render() {

        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>
                <input className="form-control" id="disabledInput" type="text" placeholder="Kişi kaydetmek için alanları doldurun" disabled/>
                    <div className="form-row">
                        <div className="form-group col-md-12 mt-4">
                            <label htmlFor="inputName">İsim:</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="name"/>
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12 mt-4">
                            <label htmlFor="inputName">Soyisim:</label>
                            <input  type="text" 
                                    className="form-control" 
                                    surname="surname"/>
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="overviewTextarea">Telefon Numarası:</label>
                            <input 
                                className="form-control" 
                                name="overview"/>
                        </div>

                    </div>
                <input type="submit" className="btn btn-success btn-block" value="Kişiyi kaydet" />
            </form>
        </div>
        )

    }
}


export default AddMovie;
