import React from 'react';
import serialize from 'form-serialize';

class AddUser extends React.Component {

    handleFormSubmit = (e) => {
        e.preventDefault();
        const newUser = serialize(e.target, { hash: true });
        //console.log(newUser);
        this.props.onAddUser(newUser);
    }

    constructor() {
        super();
        this.state = {value: 'beef'};
        this.handleValueChange = this.handleValueChange.bind(this);
      }

      handleValueChange(e) {
        this.setState({value: e.target.value});
      }

    render() {

        return  (
            <div className="container">
            <form className="mt-5" onSubmit={this.handleFormSubmit}>
                <input className="form-control" id="disabledInput" type="text" placeholder="Kişi kaydetmek için alanları doldurun" disabled/>
                <div><input type='text' value={'hello world'} onChange={this.handleValueChange}/></div>
      <div><input type='text' value={''} onChange={this.handleValueChange}/></div>
      <div><input type='text' value={this.state.value} onChange={this.handleValueChange}/></div>
      <div><input type='text' value={null} onChange={this.handleValueChange}/></div>
      <div><input type='text' value={undefined} onChange={this.handleValueChange}/></div>
      <hr/>  
      <div><input type='text' defaultValue={this.state.value} onChange={this.handleValueChange}/></div>
      <p>{this.state.value}</p>
                    <div className="form-row">
                        <div className="form-group col-md-12 mt-4">
                            <label htmlFor="inputName">İsim:</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="name" 
                            />
                        </div>


                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="inputSurName">Soyisim:</label>
                            <input  type="text" 
                                    className="form-control" 
                                    name="surname"/>
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <label htmlFor="telTextarea">Telefon Numarası:</label>
                            <input 
                                type="number" maxLength="11"
                                className="form-control" 
                                name="tel"/>
                        </div>

                    </div>
                <input type="submit" className="btn btn-success btn-block" value="Kişiyi kaydet" />
            </form>
        </div>
        )

    }
}


export default AddUser;
